package info.nukoneko.android.ho_n.controller.main.twitter.tab;import android.support.annotation.Nullable;import java.util.ArrayList;import rx.Observable;/** * Created by atsumi on 2016/10/21. */public enum NKTwitterTabListType {    Home(0),    Notification(1),    Mentions(2),    Favorites(3),    Lists(4),    Search(5),    Followers(6),    Messages(7),    User(8);    final public Integer index;    NKTwitterTabListType(Integer index){        this.index = index;    }    @Nullable    static public NKTwitterTabListType getType(int index){        for (NKTwitterTabListType type : NKTwitterTabListType.values()){            if (type.index == index) return type;        }        return null;    }    @Nullable    static public NKTwitterTabListType getType(String name){        for (NKTwitterTabListType type : NKTwitterTabListType.values()){            if (type.name().equals(name)) return type;        }        return null;    }    static public String[] getValues(){        ArrayList<String> _ret = new ArrayList<>();        _ret.addAll(Observable.from(NKTwitterTabListType.values()).map(Enum::name).toList().toBlocking().single());        String[] ret = new String[_ret.size()];        ret = _ret.toArray(ret);        return ret;    }}