package info.nukoneko.android.ho_n.sys.eventbus.event;import info.nukoneko.android.ho_n.sys.eventbus.NKEventTwitter;import twitter4j.User;/** * Created by TEJNEK on 2016/10/22. */public class OnStreamBlock extends NKEventTwitter {    private final User source;    private final User blockedUser;    public OnStreamBlock(long managingUserId, User source, User blockedUser) {        super(managingUserId);        this.source = source;        this.blockedUser = blockedUser;    }    public User getBlockedUser() {        return blockedUser;    }    public User getSource() {        return source;    }}