package info.nukoneko.android.ho_n.sys.eventbus.event;import info.nukoneko.android.ho_n.sys.eventbus.NKEventTwitter;import twitter4j.Status;import twitter4j.User;/** * Created by TEJNEK on 2016/10/22. */public class OnStreamFavoritedRetweet extends NKEventTwitter {    private final User source;    private final User target;    private final Status favoritedRetweet;    public OnStreamFavoritedRetweet(long managingUserId, User source, User target, Status favoritedRetweeet) {        super(managingUserId);        this.source = source;        this.target = target;        this.favoritedRetweet = favoritedRetweeet;    }    public User getTarget() {        return target;    }    public User getSource() {        return source;    }    public Status getFavoritedRetweet() {        return favoritedRetweet;    }}