package info.nukoneko.android.ho_n.sys.eventbus.event;import info.nukoneko.android.ho_n.sys.eventbus.NKEventTwitter;import twitter4j.User;/** * Created by TEJNEK on 2016/10/22. */public class OnStreamFollow extends NKEventTwitter {    private final User source;    private final User followedUser;    public OnStreamFollow(long managingUserId, User source, User followedUser) {        super(managingUserId);        this.source = source;        this.followedUser = followedUser;    }    public User getSource() {        return source;    }    public User getFollowedUser() {        return followedUser;    }}