package info.nukoneko.android.ho_n.sys.eventbus.event;import info.nukoneko.android.ho_n.sys.eventbus.NKEventTwitter;import twitter4j.Status;import twitter4j.User;/** * Created by TEJNEK on 2016/10/22. */public class OnStreamRetweetedRetweet extends NKEventTwitter {    private final User source;    private final User target;    private final Status retweetedStatus;    public OnStreamRetweetedRetweet(long managingUserId, User source, User target, Status retweetedStatus) {        super(managingUserId);        this.source = source;        this.target = target;        this.retweetedStatus = retweetedStatus;    }    public User getSource() {        return source;    }    public Status getRetweetedStatus() {        return retweetedStatus;    }    public User getTarget() {        return target;    }}