package info.nukoneko.android.ho_n.sys.eventbus.event;import info.nukoneko.android.ho_n.sys.eventbus.NKEventTwitter;import twitter4j.Status;import twitter4j.User;/** * Created by TEJNEK on 2016/10/22. */public class OnStreamUnfavorite extends NKEventTwitter {    private final User source;    private final User target;    private final Status unfavoritedStatus;    public OnStreamUnfavorite(long managingUserId, User source, User target, Status unfavoritedStatus) {        super(managingUserId);        this.source = source;        this.target = target;        this.unfavoritedStatus = unfavoritedStatus;    }    public Status getUnfavoritedStatus() {        return unfavoritedStatus;    }    public User getSource() {        return source;    }    public User getTarget() {        return target;    }}