package info.nukoneko.android.ho_n.sys.util.view;import android.app.ProgressDialog;import android.content.Context;import android.support.annotation.StringRes;/** * Created by atsumi on 2016/10/27. */public final class NKProgressUtil {    static public ProgressDialog createProgress(Context context, @StringRes int title) {        ProgressDialog progressDialog = new ProgressDialog(context);        progressDialog.setTitle(title);        progressDialog.setProgressStyle(ProgressDialog.STYLE_SPINNER);        return progressDialog;    }}